import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const AboutContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const AboutHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: 4rem;
`;

const AboutTitle = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: #1a1a1a;
  margin-bottom: 1rem;
  font-family: 'Montserrat', sans-serif;
`;

const AboutSubtitle = styled.p`
  font-size: 1.2rem;
  color: #757575;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const AboutContent = styled(motion.div)`
  display: grid;
  gap: 3rem;
  max-width: 800px;
  margin: 0 auto;
`;

const AboutSection = styled.div`
  background: white;
  padding: 2.5rem;
  border-radius: 1rem;
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  
  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
    transition: all 0.3s ease;
  }
`;

const SectionTitle = styled.h2`
  font-size: 1.8rem;
  font-weight: 600;
  color: #1a1a1a;
  margin-bottom: 1.5rem;
  font-family: 'Poppins', sans-serif;
`;

const SectionText = styled.p`
  font-size: 1.1rem;
  line-height: 1.8;
  color: #616161;
  margin-bottom: 1.5rem;
  font-family: 'Inter', sans-serif;
  
  &:last-child {
    margin-bottom: 0;
  }
`;

const ValuesSection = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
`;

const ValueCard = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 0.75rem;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  text-align: center;
  border-top: 4px solid #ff6b35;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
  }
`;

const ValueIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const ValueTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: #1a1a1a;
  margin-bottom: 1rem;
  font-family: 'Poppins', sans-serif;
`;

const ValueDescription = styled.p`
  font-size: 1rem;
  line-height: 1.6;
  color: #757575;
  font-family: 'Inter', sans-serif;
`;

const TeamSection = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
`;

const TeamMember = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 1rem;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  text-align: center;
  
  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
  }
`;

const MemberAvatar = styled.div`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  background: linear-gradient(135deg, #ff6b35, #ff8a65);
  margin: 0 auto 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2.5rem;
  color: white;
  font-weight: bold;
`;

const MemberName = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: #1a1a1a;
  margin-bottom: 0.5rem;
  font-family: 'Poppins', sans-serif;
`;

const MemberRole = styled.p`
  font-size: 1rem;
  color: #ff6b35;
  font-weight: 500;
  margin-bottom: 1rem;
  font-family: 'Inter', sans-serif;
`;

const MemberBio = styled.p`
  font-size: 0.95rem;
  line-height: 1.6;
  color: #757575;
  font-family: 'Inter', sans-serif;
`;

const ContactSection = styled(motion.div)`
  background: linear-gradient(135deg, #f5f5f5, #eeeeee);
  padding: 3rem;
  border-radius: 1rem;
  text-align: center;
  margin-top: 3rem;
`;

const ContactTitle = styled.h2`
  font-size: 2rem;
  font-weight: 600;
  color: #1a1a1a;
  margin-bottom: 1rem;
  font-family: 'Poppins', sans-serif;
`;

const ContactText = styled.p`
  font-size: 1.1rem;
  line-height: 1.6;
  color: #757575;
  margin-bottom: 2rem;
  font-family: 'Inter', sans-serif;
`;

const ContactButton = styled(motion.a)`
  display: inline-block;
  background: #ff6b35;
  color: white;
  padding: 1rem 2rem;
  border-radius: 9999px;
  text-decoration: none;
  font-weight: 600;
  font-size: 1.1rem;
  transition: all 0.3s ease;
  font-family: 'Inter', sans-serif;
  
  &:hover {
    background: #e64a19;
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(255, 107, 53, 0.3);
  }
`;

const About = () => {
  return (
    <AboutContainer>
      <AboutHeader
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <AboutTitle>About SoleMate</AboutTitle>
        <AboutSubtitle>
          We're passionate about bringing you the finest footwear that combines style, comfort, and quality. 
          Our journey began with a simple mission: to make every step count.
        </AboutSubtitle>
      </AboutHeader>

      <AboutContent>
        <AboutSection
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <SectionTitle>Our Story</SectionTitle>
          <SectionText>
            Founded in 2020, SoleMate emerged from a deep passion for footwear and a commitment to quality. 
            What started as a small local shop has grown into a trusted destination for shoe enthusiasts across the country.
          </SectionText>
          <SectionText>
            We believe that the right pair of shoes can transform not just your outfit, but your entire day. 
            That's why we carefully curate our collection, working with the best brands and artisans to bring you 
            footwear that exceeds expectations.
          </SectionText>
        </AboutSection>

        <AboutSection
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          <SectionTitle>Our Mission</SectionTitle>
          <SectionText>
            At SoleMate, we're committed to providing exceptional footwear that enhances your lifestyle. 
            We understand that every customer is unique, which is why we offer a diverse range of styles, 
            sizes, and options to suit every preference and need.
          </SectionText>
          <SectionText>
            Quality is at the heart of everything we do. From the materials we select to the customer service 
            we provide, we strive for excellence in every detail. Your satisfaction and comfort are our top priorities.
          </SectionText>
        </AboutSection>

        <AboutSection
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <SectionTitle>Our Values</SectionTitle>
          <ValuesSection>
            <ValueCard>
              <ValueIcon>üåü</ValueIcon>
              <ValueTitle>Quality First</ValueTitle>
              <ValueDescription>
                We never compromise on quality. Every product in our collection meets our high standards 
                for durability, comfort, and style.
              </ValueDescription>
            </ValueCard>
            
            <ValueCard>
              <ValueIcon>ü§ù</ValueIcon>
              <ValueTitle>Customer Focus</ValueTitle>
