import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const AboutContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const AboutHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: 4rem;
`;

const AboutTitle = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.black};
  margin-bottom: 1rem;
  font-family: 'Montserrat', sans-serif;
`;

const AboutSubtitle = styled.p`
  font-size: 1.2rem;
  color: ${({ theme }) => theme.colors.grey[600]};
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const AboutContent = styled(motion.div)`
  display: grid;
  gap: 3rem;
  max-width: 800px;
  margin: 0 auto;
`;

const AboutSection = styled.div`
  background: white;
  padding: 2.5rem;
  border-radius: ${({ theme }) => theme.borderRadius.xl};
  box-shadow: ${({ theme }) => theme.shadows.lg};
  
  &:hover {
    transform: translateY(-4px);
    box-shadow: ${({ theme }) => theme.shadows['2xl']};
    transition: ${({ theme }) => theme.transitions.base};
  }
`;

const SectionTitle = styled.h2`
  font-size: 1.8rem;
  font-weight: 600;
  color: ${({ theme }) => theme.colors.black};
  margin-bottom: 1.5rem;
  font-family: 'Poppins', sans-serif;
`;

const SectionText = styled.p`
  font-size: 1.1rem;
  line-height: 1.8;
  color: ${({ theme }) => theme.colors.grey[700]};
  margin-bottom: 1.5rem;
  font-family: 'Inter', sans-serif;
  
  &:last-child {
    margin-bottom: 0;
  }
`;

const ValuesSection = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
`;

const ValueCard = styled.div`
  background: white;
  padding: 2rem;
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  box-shadow: ${({ theme }) => theme.shadows.md};
  text-align: center;
  border-top: 4px solid ${({ theme }) => theme.colors.accent.primary};
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: ${({ theme }) => theme.shadows.lg};
    transition: ${({ theme }) => theme.transitions.base};
  }
`;

const ValueIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const ValueTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: ${({ theme }) => theme.colors.black};
  margin-bottom: 1rem;
  font-family: 'Poppins', sans-serif;
`;

const ValueDescription = styled.p`
  font-size: 1rem;
  line-height: 1.6;
  color: ${({ theme }) => theme.colors.grey[600]};
  font-family: 'Inter', sans-serif;
`;

const About = () => {
  return (
    <AboutContainer>
      <AboutHeader
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <AboutTitle>About SoleMate</AboutTitle>
        <AboutSubtitle>
          Where style meets comfort, and every step tells a story
        </AboutSubtitle>
      </AboutHeader>

      <AboutContent
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.2 }}
      >
        <AboutSection
          as={motion.div}
          whileHover={{ y: -4 }}
          transition={{ duration: 0.3 }}
        >
          <SectionTitle>Our Story</SectionTitle>
          <SectionText>
            At SoleMate we believe shoes are more than just an accessory. They are a reflection of your personality, your lifestyle, and your journey. Our brand was created with a simple vision: to design footwear that combines fashion, comfort, and innovation in a way that feels effortless.
          </SectionText>
          <SectionText>
            From the start our goal has been to create shoes that not only look stylish but also deliver lasting comfort and support with every step. We carefully choose high quality materials and bring them together with modern design techniques to create footwear that is durable, versatile, and ready for any occasion.
          </SectionText>
        </AboutSection>

        <AboutSection
          as={motion.div}
          whileHover={{ y: -4 }}
          transition={{ duration: 0.3 }}
        >
          <SectionTitle>Built for Your Lifestyle</SectionTitle>
          <SectionText>
            SoleMate is built for people who are always on the move. Whether you are running through the city, working out at the gym, heading into the office, or enjoying a casual weekend, our shoes are designed to adapt to your lifestyle.
          </SectionText>
          <SectionText>
            Every pair is made with attention to detail and tested for performance so you never have to choose between style and practicality.
          </SectionText>
        </AboutSection>

        <AboutSection
          as={motion.div}
          whileHover={{ y: -4 }}
          transition={{ duration: 0.3 }}
        >
          <SectionTitle>Community & Innovation</SectionTitle>
          <SectionText>
            Beyond making shoes we are focused on building a community of people who want more from their footwear. We value sustainability and responsible fashion, which is why we continue to explore eco friendly materials and ethical production practices.
          </SectionText>
          <SectionText>
            For us, innovation is not just about design but about making a positive impact on the world around us.
          </SectionText>
        </AboutSection>

        <AboutSection
          as={motion.div}
          whileHover={{ y: -4 }}
          transition={{ duration: 0.3 }}
        >
          <SectionTitle>Our Mission</SectionTitle>
          <SectionText>
            Our mission is simple. We want to be your true companion, your SoleMate, for every step of your journey. With us you are not just wearing shoes. You are stepping into confidence, freedom, and individuality.
          </SectionText>
          <SectionText>
            SoleMate is here to walk with you, run with you, and grow with you through every adventure.
          </SectionText>
        </AboutSection>
      </AboutContent>

      <ValuesSection
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.4 }}
      >
        <ValueCard
          as={motion.div}
          whileHover={{ y: -2 }}
          transition={{ duration: 0.3 }}
        >
          <ValueIcon>🎨</ValueIcon>
          <ValueTitle>Design Excellence</ValueTitle>
          <ValueDescription>
            Combining fashion-forward aesthetics with timeless appeal
          </ValueDescription>
        </ValueCard>

        <ValueCard
          as={motion.div}
          whileHover={{ y: -2 }}
          transition={{ duration: 0.3 }}
        >
          <ValueIcon>🔄</ValueIcon>
          <ValueTitle>Sustainability</ValueTitle>
          <ValueDescription>
            Committed to eco-friendly materials and ethical practices
          </ValueDescription>
        </ValueCard>

        <ValueCard
          as={motion.div}
          whileHover={{ y: -2 }}
          transition={{ duration: 0.3 }}
        >
          <ValueIcon>💪</ValueIcon>
          <ValueTitle>Quality Craftsmanship</ValueTitle>
          <ValueDescription>
            Premium materials and attention to detail in every pair
          </ValueDescription>
        </ValueCard>

        <ValueCard
          as={motion.div}
          whileHover={{ y: -2 }}
          transition={{ duration: 0.3 }}
        >
          <ValueIcon>🤝</ValueIcon>
          <ValueTitle>Community</ValueTitle>
          <ValueDescription>
            Building connections through shared passion for great footwear
          </ValueDescription>
        </ValueCard>
      </ValuesSection>
    </AboutContainer>
  );
};

export default About;
