import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion, useScroll, useTransform } from 'framer-motion';
import { FiArrowRight, FiStar, FiShoppingCart, FiEye } from 'react-icons/fi';
import { getFeaturedProducts } from '../data/products';
import ProductCard from '../components/ProductCard';

const HomeContainer = styled.div`
  min-height: 100vh;
  overflow-x: hidden;
`;

const HeroSection = styled.section`
  position: relative;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('https://images.unsplash.com/photo-1549298916-b41d501d3772?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2012&q=80') center/cover;
    opacity: 0.3;
    z-index: 1;
  }
`;

const HeroContent = styled.div`
  position: relative;
  z-index: 2;
  text-align: center;
  color: white;
  max-width: 800px;
  padding: 0 1rem;
`;

const HeroTitle = styled(motion.h1)`
  font-size: 4.5rem;
  font-weight: 800;
  margin-bottom: 2rem;
  line-height: 1.1;
  text-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
  
  @media (max-width: 768px) {
    font-size: 3rem;
  }
  
  @media (max-width: 640px) {
    font-size: 2.5rem;
  }
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 1.25rem;
  margin-bottom: 3rem;
  opacity: 0.9;
  font-weight: 500;
  
  @media (max-width: 768px) {
    font-size: 1.125rem;
  }
`;

const HeroButtons = styled(motion.div)`
  display: flex;
  gap: 2rem;
  justify-content: center;
  flex-wrap: wrap;
  
  @media (max-width: 640px) {
    flex-direction: column;
    align-items: center;
  }
`;

const PrimaryButton = styled(Link)`
  background: #ff6b35;
  color: white;
  padding: 1rem 2rem;
  border-radius: 9999px;
  text-decoration: none;
  font-weight: 600;
  font-size: 1.125rem;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  
  &:hover {
    background: #e64a19;
    transform: translateY(-3px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }
`;

const SecondaryButton = styled(Link)`
  background: transparent;
  color: white;
  padding: 1rem 2rem;
  border-radius: 9999px;
  text-decoration: none;
  font-weight: 600;
  font-size: 1.125rem;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  border: 2px solid white;
  
  &:hover {
    color: #1a1a1a;
    background: white;
    transform: translateY(-3px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }
`;

const FloatingShoe = styled(motion.div)`
  position: absolute;
  right: 10%;
  top: 50%;
  transform: translateY(-50%);
  width: 300px;
  height: 300px;
  border-radius: 1rem;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  
  @media (max-width: 768px) {
    display: none;
  }
`;

const FloatingShoeImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 1rem;
`;

const FeaturedSection = styled.section`
  padding: 6rem 0;
  
  @media (max-width: 768px) {
    padding: 4rem 0;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
  
  @media (max-width: 768px) {
    padding: 0 0.5rem;
  }
`;

const SectionTitle = styled.h2`
  text-align: center;
  margin-bottom: 3rem;
  font-size: 3rem;
  font-weight: 800;
  
  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const ProductsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2rem;
  margin-bottom: 4rem;
`;

const ProductCardWrapper = styled(motion.div)`
  background: #fafafa;
  border-radius: 1rem;
  padding: 2rem;
  text-align: center;
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }
`;

const ProductImage = styled.div`
  width: 120px;
  height: 120px;
  margin: 0 auto 1rem;
  background: linear-gradient(135deg, #ff6b3520, #ff8a6520);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  
  &:hover {
    transform: scale(1.1);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }
`;

const ProductIcon = styled.div`
  font-size: 3rem;
  color: #ff6b35;
`;

const ProductTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  color: #1a1a1a;
`;

const ProductDescription = styled.p`
  color: #757575;
  font-size: 1rem;
  margin-bottom: 1rem;
`;

const ViewButton = styled(Link)`
  background: #ff6b35;
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 9999px;
  text-decoration: none;
  font-weight: 600;
  font-size: 0.875rem;
  transition: all 0.3s ease;
  
  &:hover {
    background: #e64a19;
  }
`;

const CategoriesSection = styled.section`
  padding: 4rem 0;
  background: #f5f5f5;
`;

const CategoriesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
`;

const CategoryCard = styled(motion.div)`
  background: white;
  border-radius: 1rem;
  padding: 2rem;
  text-align: center;
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }
`;

const CategoryTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  color: #1a1a1a;
`;

const CategoryDescription = styled.p`
  color: #757575;
  font-size: 1rem;
  margin-bottom: 1rem;
`;

const CategoryLink = styled(Link)`
  color: #ff6b35;
  font-weight: 600;
  text-decoration: none;
  font-size: 0.875rem;
  
  &:hover {
    text-decoration: underline;
  }
`;

const NewsletterSection = styled.section`
  padding: 4rem 0;
  background: linear-gradient(135deg, #f5f5f5, #eeeeee);
`;

const NewsletterContent = styled.div`
  text-align: center;
  max-width: 600px;
  margin: 0 auto;
`;

const NewsletterTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: #1a1a1a;
`;

const NewsletterDescription = styled.p`
  font-size: 1.125rem;
  color: #757575;
  margin-bottom: 2rem;
`;

const NewsletterForm = styled.form`
  display: flex;
  gap: 1rem;
  max-width: 400px;
  margin: 0 auto;
  
  @media (max-width: 640px) {
    flex-direction: column;
  }
`;

const NewsletterInput = styled.input`
  flex: 1;
  padding: 1rem;
  border: 1px solid #e0e0e0;
  border-radius: 9999px;
  font-size: 1rem;
  outline: none;
  
  &:focus {
    border-color: #ff6b35;
  }
`;

const NewsletterButton = styled.button`
  background: #ff6b35;
  color: white;
  padding: 1rem 2rem;
  border: none;
  border-radius: 9999px;
  font-weight: 600;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background: #e64a19;
  }
`;

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const { scrollY } = useScroll();
  const y = useTransform(scrollY, [0, 1000], [0, -200]);

  useEffect(() => {
    const products = getFeaturedProducts();
    setFeaturedProducts(products);
  }, []);

  return (
    <HomeContainer>
      <HeroSection>
        <FloatingShoe style={{ y }}>
          <FloatingShoeImage 
            src="https://images.unsplash.com/photo-1549298916-b41d501d3772?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2012&q=80" 
            alt="Floating Shoe"
          />
        </FloatingShoe>
        
        <HeroContent>
          <HeroTitle
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Step into Style
          </HeroTitle>
          
          <HeroSubtitle
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Discover the latest trends in footwear. From casual sneakers to elegant heels, 
            we have everything you need to make a statement.
          </HeroSubtitle>
          
          <HeroButtons
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <PrimaryButton to="/products">
              Shop Now <FiArrowRight />
            </PrimaryButton>
            <SecondaryButton to="/about">
              Learn More <FiArrowRight />
            </SecondaryButton>
          </HeroButtons>
        </HeroContent>
      </HeroSection>

      <FeaturedSection>
        <Container>
          <SectionTitle>Featured Products</SectionTitle>
          <ProductsGrid>
            {featuredProducts.slice(0, 3).map((product) => (
              <ProductCardWrapper
                key={product.id}
                whileHover={{ scale: 1.05 }}
                transition={{ duration: 0.3 }}
              >
                <ProductImage>
                  <ProductIcon>👟</ProductIcon>
                </ProductImage>
                <ProductTitle>{product.name}</ProductTitle>
                <ProductDescription>{product.description}</ProductDescription>
                <ViewButton to={`/product/${product.id}`}>
                  View Details
                </ViewButton>
              </ProductCardWrapper>
            ))}
          </ProductsGrid>
        </Container>
      </FeaturedSection>

      <CategoriesSection>
        <Container>
          <SectionTitle>Shop by Category</SectionTitle>
          <CategoriesGrid>
            <CategoryCard
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.3 }}
            >
              <CategoryTitle>Running Shoes</CategoryTitle>
              <CategoryDescription>
                Performance footwear for every runner, from beginners to marathon champions.
              </CategoryDescription>
              <CategoryLink to="/products">Explore →</CategoryLink>
            </CategoryCard>
            
            <CategoryCard
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.3 }}
            >
              <CategoryTitle>Casual Sneakers</CategoryTitle>
              <CategoryDescription>
                Comfortable and stylish sneakers for everyday wear and casual outings.
              </CategoryDescription>
              <CategoryLink to="/products">Explore →</CategoryLink>
            </CategoryCard>
            
            <CategoryCard
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.3 }}
            >
              <CategoryTitle>Formal Shoes</CategoryTitle>
              <CategoryDescription>
                Elegant footwear for professional settings and special occasions.
              </CategoryDescription>
              <CategoryLink to="/products">Explore →</CategoryLink>
            </CategoryCard>
          </CategoriesGrid>
        </Container>
      </CategoriesSection>

      <NewsletterSection>
        <Container>
          <NewsletterContent>
            <NewsletterTitle>Stay in the Loop</NewsletterTitle>
            <NewsletterDescription>
              Get the latest updates on new arrivals, exclusive offers, and style tips.
            </NewsletterDescription>
            <NewsletterForm>
              <NewsletterInput 
                type="email" 
                placeholder="Enter your email address"
                required
              />
              <NewsletterButton type="submit">Subscribe</NewsletterButton>
            </NewsletterForm>
          </NewsletterContent>
        </Container>
      </NewsletterSection>
    </HomeContainer>
  );
};

export default Home;
