export const products = [
  {
    id: 1,
    name: "New Balance 9060",
    category: "sneaker",
    price: 189.99,
    originalPrice: 209.99,
    image: "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcT6TrF38Bk9lXvF50FOSC6III2RoCEh0nCCoTRUzNl3AQW5zbsA56D9kzojJtQEMt7R1ni1aMh0uL-se4X7tmonlmpTzhMbzv6OVTvGvv_WcXI0Dk12mGC2ifY&w=300&h=300",
    images: [
      "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcT6TrF38Bk9lXvF50FOSC6III2RoCEh0nCCoTRUzNl3AQW5zbsA56D9kzojJtQEMt7R1ni1aMh0uL-se4X7tmonlmpTzhMbzv6OVTvGvv_WcXI0Dk12mGC2ifY&w=300&h=300",
      "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcT6TrF38Bk9lXvF50FOSC6III2RoCEh0nCCoTRUzNl3AQW5zbsA56D9kzojJtQEMt7R1ni1aMh0uL-se4X7tmonlmpTzhMbzv6OVTvGvv_WcXI0Dk12mGC2ifY&w=300&h=300",
      "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcT6TrF38Bk9lXvF50FOSC6III2RoCEh0nCCoTRUzNl3AQW5zbsA56D9kzojJtQEMt7R1ni1aMh0uL-se4X7tmonlmpTzhMbzv6OVTvGvv_WcXI0Dk12mGC2ifY&w=300&h=300"
    ],
    description: "The New Balance 9060 features a modern, chunky design with multi-layered construction. This lifestyle sneaker combines light brown suede panels with cream mesh sections, featuring the iconic 'N' logo and a sculpted midsole with a distinctive wavy design.",
    features: ["Premium suede upper", "Breathable mesh panels", "Sculpted midsole", "Chunky dad shoe design", "Iconic N logo"],
    sizes: [7, 7.5, 8, 8.5, 9, 9.5, 10, 10.5, 11, 11.5, 12, 13],
    colors: ["Light Brown/Cream", "Tan/Off-White", "Brown/Blue"],
    rating: 4.8,
    reviews: 892,
    inStock: true,
    featured: true
  }
];

export const categories = [
  { id: "all", name: "All Shoes", count: products.length },
  { id: "sneaker", name: "Sneaker", count: products.filter(p => p.category === "sneaker").length },
  { id: "running", name: "Running", count: products.filter(p => p.category === "running").length },
  { id: "casual", name: "Casual", count: products.filter(p => p.category === "casual").length },
  { id: "basketball", name: "Basketball", count: products.filter(p => p.category === "basketball").length }
];

export const getProductById = (id) => {
  return products.find(product => product.id === parseInt(id));
};

export const getProductsByCategory = (category) => {
  if (category === "all") return products;
  return products.filter(product => product.category === category);
};

export const getFeaturedProducts = () => {
  return products.filter(product => product.featured);
};
