import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiHeart, FiShoppingCart, FiEye, FiStar } from 'react-icons/fi';
import { useCart } from '../context/CartContext';

const Card = styled(motion.div)`
  background: white;
  border-radius: ${({ theme }) => theme.borderRadius.xl};
  overflow: hidden;
  box-shadow: ${({ theme }) => theme.shadows.lg};
  transition: ${({ theme }) => theme.transitions.base};
  position: relative;
  
  &:hover {
    transform: translateY(-8px);
    box-shadow: ${({ theme }) => theme.shadows['2xl']};
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  overflow: hidden;
  background: ${({ theme }) => theme.colors.grey[100]};
`;

const ProductImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: ${({ theme }) => theme.transitions.base};
  
  ${Card}:hover & {
    transform: scale(1.1);
  }
`;

const WishlistButton = styled(motion.button)`
  position: absolute;
  top: ${({ theme }) => theme.spacing.md};
  right: ${({ theme }) => theme.spacing.md};
  background: white;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: ${({ theme }) => theme.borderRadius.full};
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  box-shadow: ${({ theme }) => theme.shadows.md};
  color: ${({ theme, isWishlisted }) => 
    isWishlisted ? theme.colors.accent.primary : theme.colors.grey[600]};
  transition: ${({ theme }) => theme.transitions.base};
  z-index: 2;
  
  &:hover {
    background: ${({ theme }) => theme.colors.accent.primary};
    color: white;
    transform: scale(1.1);
  }
`;

const DiscountBadge = styled.span`
  position: absolute;
  top: ${({ theme }) => theme.spacing.md};
  left: ${({ theme }) => theme.spacing.md};
  background: ${({ theme }) => theme.colors.accent.primary};
  color: white;
  padding: ${({ theme }) => theme.spacing.xs} ${({ theme }) => theme.spacing.sm};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-size: ${({ theme }) => theme.typography.sizes.sm};
  font-weight: ${({ theme }) => theme.typography.weights.bold};
  z-index: 2;
`;

const CardContent = styled.div`
  padding: ${({ theme }) => theme.spacing.lg};
`;

const Category = styled.span`
  display: inline-block;
  background: ${({ theme }) => theme.colors.accent.light};
  color: ${({ theme }) => theme.colors.accent.dark};
  padding: ${({ theme }) => theme.spacing.xs} ${({ theme }) => theme.spacing.sm};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-size: ${({ theme }) => theme.typography.sizes.sm};
  font-weight: ${({ theme }) => theme.typography.weights.medium};
  margin-bottom: ${({ theme }) => theme.spacing.sm};
`;

const ProductName = styled.h3`
  font-size: ${({ theme }) => theme.typography.sizes.lg};
  font-weight: ${({ theme }) => theme.typography.weights.bold};
  color: ${({ theme }) => theme.colors.black};
  margin-bottom: ${({ theme }) => theme.spacing.sm};
  line-height: 1.3;
  
  ${Card}:hover & {
    color: ${({ theme }) => theme.colors.accent.primary};
  }
`;

const Rating = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing.xs};
  margin-bottom: ${({ theme }) => theme.spacing.sm};
`;

const Stars = styled.div`
  display: flex;
  gap: 2px;
  color: ${({ theme }) => theme.colors.accent.primary};
`;

const ReviewCount = styled.span`
  font-size: ${({ theme }) => theme.typography.sizes.sm};
  color: ${({ theme }) => theme.colors.grey[600]};
`;

const PriceContainer = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing.sm};
  margin-bottom: ${({ theme }) => theme.spacing.lg};
`;

const CurrentPrice = styled.span`
  font-size: ${({ theme }) => theme.typography.sizes.xl};
  font-weight: ${({ theme }) => theme.typography.weights.bold};
  color: ${({ theme }) => theme.colors.accent.primary};
`;

const OriginalPrice = styled.span`
  font-size: ${({ theme }) => theme.typography.sizes.base};
  color: ${({ theme }) => theme.colors.grey[500]};
  text-decoration: line-through;
`;

const Actions = styled.div`
  display: flex;
  gap: ${({ theme }) => theme.spacing.sm};
`;

const AddToCartButton = styled(motion.button)`
  flex: 1;
  background: ${({ theme }) => theme.colors.accent.primary};
  color: white;
  border: none;
  padding: ${({ theme }) => theme.spacing.md};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-weight: ${({ theme }) => theme.typography.weights.semibold};
  cursor: pointer;
  transition: ${({ theme }) => theme.transitions.base};
  display: flex;
  align-items: center;
  justify-content: center;
  gap: ${({ theme }) => theme.spacing.xs};
  
  &:hover {
    background: ${({ theme }) => theme.colors.accent.dark};
    transform: translateY(-2px);
  }
  
  &:disabled {
    background: ${({ theme }) => theme.colors.grey[400]};
    cursor: not-allowed;
    transform: none;
  }
`;

const ViewDetailsButton = styled(Link)`
  background: transparent;
  color: ${({ theme }) => theme.colors.grey[700]};
  border: 2px solid ${({ theme }) => theme.colors.grey[300]};
  padding: ${({ theme }) => theme.spacing.md};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  text-decoration: none;
  font-weight: ${({ theme }) => theme.typography.weights.semibold};
  cursor: pointer;
  transition: ${({ theme }) => theme.transitions.base};
  display: flex;
  align-items: center;
  justify-content: center;
  gap: ${({ theme }) => theme.spacing.xs};
  
  &:hover {
    border-color: ${({ theme }) => theme.colors.accent.primary};
    color: ${({ theme }) => theme.colors.accent.primary};
    transform: translateY(-2px);
  }
`;

const FloatingShoe = styled(motion.div)`
  position: absolute;
  width: 60px;
  height: 60px;
  background: ${({ theme }) => theme.colors.accent.primary};
  border-radius: ${({ theme }) => theme.borderRadius.full};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: ${({ theme }) => theme.typography.sizes.xl};
  box-shadow: ${({ theme }) => theme.shadows.lg};
  z-index: 3;
  pointer-events: none;
`;

const ProductCard = ({ product }) => {
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [showFloatingShoe, setShowFloatingShoe] = useState(false);
  const { addToCart } = useCart();

  const handleAddToCart = async () => {
    setIsAddingToCart(true);
    setShowFloatingShoe(true);
    
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 500));
    
    addToCart({
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.images[0],
      size: product.sizes[0],
      color: product.colors[0],
      quantity: 1
    });
    
    setIsAddingToCart(false);
    
    // Hide floating shoe after animation
    setTimeout(() => setShowFloatingShoe(false), 1000);
  };

  const handleWishlist = () => {
    setIsWishlisted(!isWishlisted);
  };

  const hasDiscount = product.originalPrice && product.originalPrice > product.price;
  const discountPercentage = hasDiscount 
    ? Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)
    : 0;

  return (
    <Card
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
      viewport={{ once: true }}
      whileHover={{ y: -8 }}
    >
      {showFloatingShoe && (
        <FloatingShoe
          initial={{ scale: 0, y: 0 }}
          animate={{ 
            scale: [0, 1, 1, 0],
            y: [0, -100, -100, -200],
            x: [0, 0, 50, 100]
          }}
          transition={{ duration: 1, ease: "easeInOut" }}
        >
          👟
        </FloatingShoe>
      )}
      
      <ImageContainer>
        <ProductImage src={product.images[0]} alt={product.name} />
        
        {hasDiscount && (
          <DiscountBadge>-{discountPercentage}%</DiscountBadge>
        )}
        
        <WishlistButton
          onClick={handleWishlist}
          isWishlisted={isWishlisted}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <FiHeart />
        </WishlistButton>
      </ImageContainer>
      
      <CardContent>
        <Category>{product.category}</Category>
        <ProductName>{product.name}</ProductName>
        
        <Rating>
          <Stars>
            {[...Array(5)].map((_, i) => (
              <FiStar
                key={i}
                fill={i < Math.floor(product.rating) ? 'currentColor' : 'none'}
              />
            ))}
          </Stars>
          <ReviewCount>({product.reviews} reviews)</ReviewCount>
        </Rating>
        
        <PriceContainer>
          <CurrentPrice>${product.price}</CurrentPrice>
          {hasDiscount && (
            <OriginalPrice>${product.originalPrice}</OriginalPrice>
          )}
        </PriceContainer>
        
        <Actions>
          <AddToCartButton
            onClick={handleAddToCart}
            disabled={isAddingToCart}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            {isAddingToCart ? (
              <div className="loading" />
            ) : (
              <>
                <FiShoppingCart />
                Add to Cart
              </>
            )}
          </AddToCartButton>
          
          <ViewDetailsButton to={`/product/${product.id}`}>
            <FiEye />
            View
          </ViewDetailsButton>
        </Actions>
      </CardContent>
    </Card>
  );
};

export default ProductCard;
