import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiSearch, FiShoppingCart, FiUser, FiMenu, FiX, FiSun, FiMoon } from 'react-icons/fi';
import { useCart } from '../context/CartContext';
import { useTheme } from '../context/ThemeContext';

// Simplified styled components
const HeaderContainer = styled.header`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background: ${props => props.isDarkMode ? 'rgba(26, 26, 26, 0.95)' : 'rgba(255, 255, 255, 0.95)'};
  backdrop-filter: blur(20px);
  border-bottom: 1px solid ${props => props.isDarkMode ? '#424242' : '#e0e0e0'};
  z-index: 1000;
  transition: all 0.3s ease;
`;

const Nav = styled.nav`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
`;

const Logo = styled(Link)`
  font-family: 'Montserrat', sans-serif;
  font-size: 1.5rem;
  font-weight: 800;
  color: ${props => props.isDarkMode ? '#ffffff' : '#1a1a1a'};
  text-decoration: none;
  letter-spacing: -0.025em;
  transition: color 0.3s ease;
  
  &:hover {
    color: #ff6b35;
  }
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  gap: 2rem;
  
  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled(Link)`
  color: ${props => props.isDarkMode ? '#bdbdbd' : '#616161'};
  text-decoration: none;
  font-weight: 500;
  font-size: 1rem;
  transition: all 0.3s ease;
  position: relative;
  
  &:hover {
    color: #ff6b35;
  }
`;

const SearchBar = styled.div`
  position: relative;
  margin: 0 1.5rem;
  
  @media (max-width: 768px) {
    display: none;
  }
`;

const SearchInput = styled.input`
  width: 300px;
  padding: 0.75rem 1rem;
  border: 1px solid ${props => props.isDarkMode ? '#424242' : '#e0e0e0'};
  border-radius: 9999px;
  font-size: 0.875rem;
  outline: none;
  transition: all 0.3s ease;
  background: ${props => props.isDarkMode ? '#424242' : '#ffffff'};
  color: ${props => props.isDarkMode ? '#ffffff' : '#1a1a1a'};
  
  &::placeholder {
    color: ${props => props.isDarkMode ? '#9e9e9e' : '#757575'};
  }
  
  &:focus {
    border-color: #ff6b35;
    box-shadow: 0 0 0 3px rgba(255, 107, 53, 0.1);
  }
`;

const RightSection = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const Button = styled.button`
  background: none;
  border: none;
  padding: 0.5rem;
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
  color: ${props => props.isDarkMode ? '#bdbdbd' : '#616161'};
  
  &:hover {
    background: ${props => props.isDarkMode ? '#424242' : '#f5f5f5'};
    color: #ff6b35;
  }
`;

const CartButton = styled(motion.button)`
  background: none;
  border: none;
  padding: 0.5rem;
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
  color: ${props => props.isDarkMode ? '#bdbdbd' : '#616161'};
  position: relative;
  
  &:hover {
    background: ${props => props.isDarkMode ? '#424242' : '#f5f5f5'};
    color: #ff6b35;
  }
`;

const CartBadge = styled.span`
  position: absolute;
  top: -5px;
  right: -5px;
  background: #ff6b35;
  color: white;
  font-size: 0.75rem;
  font-weight: 600;
  padding: 0.25rem 0.5rem;
  border-radius: 9999px;
  min-width: 20px;
  text-align: center;
`;

const Header = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const { isDarkMode, toggleDarkMode } = useTheme();
  const cartContext = useCart();
  const navigate = useNavigate();

  // Safety check for cart context
  const cartItems = cartContext?.items || [];
  const cartItemCount = cartItems.reduce((total, item) => total + (item?.quantity || 0), 0);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/products?search=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery('');
    }
  };

  return (
    <HeaderContainer isDarkMode={isDarkMode}>
      <Nav>
        <Logo to="/" isDarkMode={isDarkMode}>
          SoleMate
        </Logo>

        <NavLinks>
          <NavLink to="/" isDarkMode={isDarkMode}>Home</NavLink>
          <NavLink to="/products" isDarkMode={isDarkMode}>Shop</NavLink>
          <NavLink to="/products" isDarkMode={isDarkMode}>Categories</NavLink>
          <NavLink to="/about" isDarkMode={isDarkMode}>About</NavLink>
        </NavLinks>

        <SearchBar>
          <form onSubmit={handleSearch}>
            <SearchInput
              type="text"
              placeholder="Search for shoes..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              isDarkMode={isDarkMode}
            />
          </form>
        </SearchBar>

        <RightSection>
          <Button onClick={toggleDarkMode} isDarkMode={isDarkMode}>
            {isDarkMode ? <FiSun /> : <FiMoon />}
          </Button>
          
          <Button isDarkMode={isDarkMode}>
            <FiUser />
          </Button>
          
          <CartButton
            onClick={() => navigate('/cart')}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            isDarkMode={isDarkMode}
          >
            <FiShoppingCart />
            {cartItemCount > 0 && <CartBadge>{cartItemCount}</CartBadge>}
          </CartButton>
        </RightSection>
      </Nav>
    </HeaderContainer>
  );
};

export default Header;
