import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiSearch, FiShoppingCart, FiUser, FiMenu, FiX, FiSun, FiMoon } from 'react-icons/fi';
import { useCart } from '../context/CartContext';

const HeaderContainer = styled.header`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background: ${({ theme, isDarkMode }) => 
    isDarkMode ? 'rgba(26, 26, 26, 0.95)' : 'rgba(255, 255, 255, 0.95)'};
  backdrop-filter: blur(20px);
  border-bottom: 1px solid ${({ theme, isDarkMode }) => 
    isDarkMode ? theme.colors.grey[800] : theme.colors.grey[200]};
  z-index: ${({ theme }) => theme.zIndex.sticky};
  transition: ${({ theme }) => theme.transitions.base};
`;

const Nav = styled.nav`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${({ theme }) => theme.spacing.md};
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: 0 ${({ theme }) => theme.spacing.sm};
    height: 70px;
  }
`;

const Logo = styled(Link)`
  font-family: ${({ theme }) => theme.typography.fonts.heading};
  font-size: ${({ theme }) => theme.typography.sizes['2xl']};
  font-weight: ${({ theme }) => theme.typography.weights.extrabold};
  color: ${({ theme, isDarkMode }) => 
    isDarkMode ? theme.colors.white : theme.colors.black};
  text-decoration: none;
  letter-spacing: ${({ theme }) => theme.typography.letterSpacing.tight};
  
  &:hover {
    color: ${({ theme }) => theme.colors.accent.primary};
  }
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: ${({ theme }) => theme.typography.sizes.xl};
  }
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing.xl};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    display: none;
  }
`;

const NavLink = styled(Link)`
  color: ${({ theme, isDarkMode }) => 
    isDarkMode ? theme.colors.grey[300] : theme.colors.grey[700]};
  text-decoration: none;
  font-weight: ${({ theme }) => theme.typography.weights.medium};
  font-size: ${({ theme }) => theme.typography.sizes.base};
  transition: ${({ theme }) => theme.transitions.base};
  position: relative;
  
  &:hover {
    color: ${({ theme }) => theme.colors.accent.primary};
  }
  
  &::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    width: 0;
    height: 2px;
    background: ${({ theme }) => theme.colors.accent.primary};
    transition: ${({ theme }) => theme.transitions.base};
  }
  
  &:hover::after {
    width: 100%;
  }
`;

const SearchBar = styled.div`
  position: relative;
  margin: 0 ${({ theme }) => theme.spacing.lg};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    display: none;
  }
`;

const SearchInput = styled.input`
  width: 300px;
  padding: ${({ theme }) => theme.spacing.sm} ${({ theme }) => theme.spacing.md};
  padding-left: ${({ theme }) => theme.spacing.xl};
  border: 2px solid ${({ theme, isDarkMode }) => 
    isDarkMode ? theme.colors.grey[700] : theme.colors.grey[200]};
  border-radius: ${({ theme }) => theme.borderRadius.full};
  background: ${({ theme, isDarkMode }) => 
    isDarkMode ? theme.colors.grey[800] : theme.colors.white};
  color: ${({ theme, isDarkMode }) => 
