  &:hover {
    color: ${({ theme }) => theme.colors.accent.primary};
    transform: scale(1.1);
  }
`;

const CloseButton = styled.button`
  position: absolute;
  top: ${({ theme }) => theme.spacing.xl};
  right: ${({ theme }) => theme.spacing.xl};
  background: none;
  border: none;
  color: ${({ theme, isDarkMode }) => 
    isDarkMode ? theme.colors.white : theme.colors.black};
  font-size: ${({ theme }) => theme.typography.sizes['2xl']};
  cursor: pointer;
  padding: ${({ theme }) => theme.spacing.sm};
`;

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const { cartItems } = useCart();
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/products?search=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery('');
    }
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    // Here you would typically update a global theme context
    // For now, we'll just toggle the local state
  };

  const cartItemCount = cartItems.reduce((total, item) => total + item.quantity, 0);

  return (
    <HeaderContainer isDarkMode={isDarkMode}>
      <Nav>
        <Logo to="/" isDarkMode={isDarkMode}>
          SoleMate
        </Logo>

        <NavLinks>
          <NavLink to="/" isDarkMode={isDarkMode}>Home</NavLink>
          <NavLink to="/products" isDarkMode={isDarkMode}>Shop</NavLink>
          <NavLink to="/products" isDarkMode={isDarkMode}>Categories</NavLink>
          <NavLink to="/products" isDarkMode={isDarkMode}>About</NavLink>
        </NavLinks>

        <SearchBar>
          <form onSubmit={handleSearch}>
            <SearchInput
              type="text"
              placeholder="Search for shoes..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              isDarkMode={isDarkMode}
            />
            <SearchIcon isDarkMode={isDarkMode} />
          </form>
        </SearchBar>

        <RightSection>
          <ThemeToggle onClick={toggleDarkMode} isDarkMode={isDarkMode}>
            {isDarkMode ? <FiSun /> : <FiMoon />}
          </ThemeToggle>
          
          <UserButton isDarkMode={isDarkMode}>
            <FiUser />
          </UserButton>
          
          <CartButton
            onClick={() => navigate('/cart')}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            isDarkMode={isDarkMode}
          >
            <FiShoppingCart />
            {cartItemCount > 0 && <CartBadge>{cartItemCount}</CartBadge>}
          </CartButton>

          <MobileMenuButton
            onClick={() => setIsMobileMenuOpen(true)}
            isDarkMode={isDarkMode}
          >
            <FiMenu />
          </MobileMenuButton>
        </RightSection>
      </Nav>

      {isMobileMenuOpen && (
        <MobileMenu
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          isDarkMode={isDarkMode}
        >
          <CloseButton
            onClick={() => setIsMobileMenuOpen(false)}
            isDarkMode={isDarkMode}
          >
            <FiX />
          </CloseButton>
          
          <MobileNavLink to="/" isDarkMode={isDarkMode} onClick={() => setIsMobileMenuOpen(false)}>
            Home
          </MobileNavLink>
          <MobileNavLink to="/products" isDarkMode={isDarkMode} onClick={() => setIsMobileMenuOpen(false)}>
            Shop
          </MobileNavLink>
          <MobileNavLink to="/products" isDarkMode={isDarkMode} onClick={() => setIsMobileMenuOpen(false)}>
            Categories
          </MobileNavLink>
          <MobileNavLink to="/products" isDarkMode={isDarkMode} onClick={() => setIsMobileMenuOpen(false)}>
            About
          </MobileNavLink>
        </MobileMenu>
      )}
    </HeaderContainer>
  );
};

export default Header;
