import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiHeart, FiShoppingCart, FiEye, FiStar } from 'react-icons/fi';
import { useCart } from '../context/CartContext';

const Card = styled(motion.div)`
  background: white;
  border-radius: 1rem;
  overflow: hidden;
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  position: relative;
  
  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 280px;
  overflow: hidden;
  background: #f5f5f5;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ProductImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
  transition: all 0.3s ease;
  padding: 1rem;
  
  ${Card}:hover & {
    transform: scale(1.05);
  }
`;

const WishlistButton = styled(motion.button)`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: white;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  color: ${({ isWishlisted }) => isWishlisted ? '#ff6b35' : '#757575'};
  transition: all 0.3s ease;
  z-index: 2;
  
  &:hover {
    background: #ff6b35;
    color: white;
    transform: scale(1.1);
  }
`;

const DiscountBadge = styled.span`
  position: absolute;
  top: 1rem;
  left: 1rem;
  background: #ff6b35;
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: 0.5rem;
  font-size: 0.875rem;
  font-weight: 700;
  z-index: 2;
`;

const CardContent = styled.div`
  padding: 2rem;
`;

const Category = styled.span`
  display: inline-block;
  background: #ffccbc;
  color: #e64a19;
  padding: 0.25rem 0.5rem;
  border-radius: 0.5rem;
  font-size: 0.875rem;
  font-weight: 500;
  margin-bottom: 0.5rem;
`;

const ProductName = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #1a1a1a;
  margin-bottom: 0.5rem;
  line-height: 1.4;
`;

const Rating = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;
`;

const Stars = styled.div`
  display: flex;
  gap: 0.125rem;
  color: #ffc107;
`;

const RatingText = styled.span`
  font-size: 0.875rem;
  color: #757575;
`;

const PriceContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1.5rem;
`;

const CurrentPrice = styled.span`
  font-size: 1.25rem;
  font-weight: 700;
  color: #1a1a1a;
`;

const OriginalPrice = styled.span`
  font-size: 1rem;
  color: #9e9e9e;
  text-decoration: line-through;
`;

const Actions = styled.div`
  display: flex;
  gap: 0.75rem;
`;

const ActionButton = styled(motion.button)`
  flex: 1;
  padding: 0.75rem;
  border: none;
  border-radius: 0.5rem;
  font-weight: 600;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
`;

const AddToCartButton = styled(ActionButton)`
  background: #ff6b35;
  color: white;
  
  &:hover {
    background: #e64a19;
    transform: translateY(-2px);
  }
`;

const ViewButton = styled(ActionButton)`
  background: #f5f5f5;
  color: #1a1a1a;
  
  &:hover {
    background: #e0e0e0;
    transform: translateY(-2px);
  }
`;

const QuickViewButton = styled(motion.button)`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) scale(0);
  background: #ff6b35;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 9999px;
  font-weight: 600;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 3;
  
  ${Card}:hover & {
    transform: translate(-50%, -50%) scale(1);
  }
  
  &:hover {
    background: #e64a19;
    transform: translate(-50%, -50%) scale(1.1);
  }
`;

const ProductCard = ({ product }) => {
  const [isWishlisted, setIsWishlisted] = useState(false);
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    addToCart(product);
  };

  const handleWishlistToggle = () => {
    setIsWishlisted(!isWishlisted);
  };

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<FiStar key={i} fill="currentColor" />);
    }

    if (hasHalfStar) {
      stars.push(<FiStar key="half" style={{ opacity: 0.5 }} />);
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<FiStar key={`empty-${i}`} style={{ opacity: 0.3 }} />);
    }

    return stars;
  };

  return (
    <Card
      whileHover={{ y: -8 }}
      transition={{ duration: 0.3 }}
    >
      <ImageContainer>
        <ProductImage src={product.image} alt={product.name} />
        
        {product.discount && (
          <DiscountBadge>-{product.discount}%</DiscountBadge>
        )}
        
        <WishlistButton
          onClick={handleWishlistToggle}
          isWishlisted={isWishlisted}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <FiHeart />
        </WishlistButton>
        
        <QuickViewButton
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <FiEye /> Quick View
        </QuickViewButton>
      </ImageContainer>

      <CardContent>
        <Category>{product.category}</Category>
        
        <ProductName>{product.name}</ProductName>
        
        <Rating>
          <Stars>
            {renderStars(product.rating)}
          </Stars>
          <RatingText>({product.rating})</RatingText>
        </Rating>

        <PriceContainer>
          <CurrentPrice>${product.price}</CurrentPrice>
          {product.originalPrice && (
            <OriginalPrice>${product.originalPrice}</OriginalPrice>
          )}
        </PriceContainer>

        <Actions>
          <AddToCartButton
            onClick={handleAddToCart}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FiShoppingCart />
            Add to Cart
          </AddToCartButton>
          
          <ViewButton
            as={Link}
            to={`/product/${product.id}`}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FiEye />
            View
          </ViewButton>
        </Actions>
      </CardContent>
    </Card>
  );
};

export default ProductCard;
