import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import { theme } from './styles/theme';
import GlobalStyles from './styles/GlobalStyles';
import { CartProvider } from './context/CartContext';
import Header from './components/Header';
import './App.css';

// Simple test components
const TestHome = () => (
  <div style={{ padding: '2rem', textAlign: 'center' }}>
    <h1>üè† Home Page</h1>
    <p>This is the home page - working correctly!</p>
  </div>
);

const TestProducts = () => (
  <div style={{ padding: '2rem', textAlign: 'center' }}>
    <h1>üõçÔ∏è Products Page</h1>
    <p>This is the products page - working correctly!</p>
  </div>
);

const TestCart = () => (
  <div style={{ padding: '2rem', textAlign: 'center' }}>
    <h1>üõí Cart Page</h1>
    <p>This is the cart page - working correctly!</p>
  </div>
);

const TestFooter = () => (
  <footer style={{ 
    background: '#333', 
    color: 'white', 
    padding: '1rem', 
    textAlign: 'center',
    position: 'fixed',
    bottom: 0,
    width: '100%'
  }}>
    <p>¬© 2024 SoleMate. All rights reserved.</p>
  </footer>
);

function App() {
  return (
    <CartProvider>
      <ThemeProvider theme={theme}>
        <GlobalStyles />
        <Router>
          <div className="App">
            <Header />
            <main style={{ minHeight: '80vh', paddingBottom: '100px', marginTop: '80px' }}>
              <Routes>
                <Route path="/" element={<TestHome />} />
                <Route path="/products" element={<TestProducts />} />
                <Route path="/cart" element={<TestCart />} />
              </Routes>
            </main>
            <TestFooter />
          </div>
        </Router>
      </ThemeProvider>
    </CartProvider>
  );
}

export default App;
