import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion, useScroll, useTransform } from 'framer-motion';
import { FiArrowRight, FiStar, FiShoppingCart, FiEye } from 'react-icons/fi';
import { getFeaturedProducts } from '../data/products';
import ProductCard from '../components/ProductCard';

const HomeContainer = styled.div`
  min-height: 100vh;
  overflow-x: hidden;
`;

const HeroSection = styled.section`
  position: relative;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('https://images.unsplash.com/photo-1549298916-b41d501d3772?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2012&q=80') center/cover;
    opacity: 0.3;
    z-index: 1;
  }
`;

const HeroContent = styled.div`
  position: relative;
  z-index: 2;
  text-align: center;
  color: white;
  max-width: 800px;
  padding: 0 ${({ theme }) => theme.spacing.md};
`;

const HeroTitle = styled(motion.h1)`
  font-size: ${({ theme }) => theme.typography.sizes['7xl']};
  font-weight: ${({ theme }) => theme.typography.weights.extrabold};
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  line-height: 1.1;
  text-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: ${({ theme }) => theme.typography.sizes['5xl']};
  }
  
  @media (max-width: ${({ theme }) => theme.breakpoints.sm}) {
    font-size: ${({ theme }) => theme.typography.sizes['4xl']};
  }
`;

const HeroSubtitle = styled(motion.p)`
  font-size: ${({ theme }) => theme.typography.sizes.xl};
  margin-bottom: ${({ theme }) => theme.spacing['2xl']};
  opacity: 0.9;
  font-weight: ${({ theme }) => theme.typography.weights.medium};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: ${({ theme }) => theme.typography.sizes.lg};
  }
`;

const HeroButtons = styled(motion.div)`
  display: flex;
  gap: ${({ theme }) => theme.spacing.lg};
  justify-content: center;
  flex-wrap: wrap;
  
  @media (max-width: ${({ theme }) => theme.breakpoints.sm}) {
    flex-direction: column;
    align-items: center;
  }
`;

const PrimaryButton = styled(Link)`
  background: ${({ theme }) => theme.colors.accent.primary};
  color: white;
  padding: ${({ theme }) => theme.spacing.md} ${({ theme }) => theme.spacing.xl};
  border-radius: ${({ theme }) => theme.borderRadius.full};
  text-decoration: none;
  font-weight: ${({ theme }) => theme.typography.weights.semibold};
  font-size: ${({ theme }) => theme.typography.sizes.lg};
  transition: ${({ theme }) => theme.transitions.base};
  display: inline-flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing.sm};
  
  &:hover {
    background: ${({ theme }) => theme.colors.accent.dark};
    transform: translateY(-3px);
    box-shadow: ${({ theme }) => theme.shadows.xl};
  }
`;

const SecondaryButton = styled(Link)`
  background: transparent;
  color: white;
  padding: ${({ theme }) => theme.spacing.md} ${({ theme }) => theme.spacing.xl};
  border: 2px solid white;
  border-radius: ${({ theme }) => theme.borderRadius.full};
  text-decoration: none;
  font-weight: ${({ theme }) => theme.typography.weights.semibold};
  font-size: ${({ theme }) => theme.typography.sizes.lg};
  transition: ${({ theme }) => theme.transitions.base};
  display: inline-flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing.sm};
  
  &:hover {
    background: white;
    color: ${({ theme }) => theme.colors.black};
    transform: translateY(-3px);
    box-shadow: ${({ theme }) => theme.shadows.xl};
  }
`;

const FloatingShoe = styled(motion.div)`
  position: absolute;
  width: 200px;
  height: 200px;
  background: url('https://images.unsplash.com/photo-1549298916-b41d501d3772?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2012&q=80') center/cover;
  border-radius: ${({ theme }) => theme.borderRadius['2xl']};
  box-shadow: ${({ theme }) => theme.shadows['2xl']};
  z-index: 1;
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    display: none;
  }
`;

const Section = styled.section`
  padding: ${({ theme }) => theme.spacing['4xl']} 0;
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: ${({ theme }) => theme.spacing['3xl']} 0;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${({ theme }) => theme.spacing.md};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: 0 ${({ theme }) => theme.spacing.sm};
  }
`;

const SectionTitle = styled(motion.h2)`
  text-align: center;
  margin-bottom: ${({ theme }) => theme.spacing['2xl']};
  font-size: ${({ theme }) => theme.typography.sizes['5xl']};
  font-weight: ${({ theme }) => theme.typography.weights.extrabold};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: ${({ theme }) => theme.typography.sizes['4xl']};
  }
`;

const FeaturedGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: ${({ theme }) => theme.spacing.xl};
  margin-bottom: ${({ theme }) => theme.spacing['3xl']};
`;

const CategorySection = styled(Section)`
  background: ${({ theme }) => theme.colors.grey[50]};
`;

const CategoryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${({ theme }) => theme.spacing.xl};
`;

const CategoryCard = styled(motion.div)`
  background: white;
  border-radius: ${({ theme }) => theme.borderRadius.xl};
  padding: ${({ theme }) => theme.spacing.xl};
  text-align: center;
  box-shadow: ${({ theme }) => theme.shadows.lg};
  cursor: pointer;
  transition: ${({ theme }) => theme.transitions.base};
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, ${({ theme }) => theme.colors.accent.primary}20, ${({ theme }) => theme.colors.accent.secondary}20);
    opacity: 0;
    transition: ${({ theme }) => theme.transitions.base};
  }
  
  &:hover {
    transform: translateY(-8px);
    box-shadow: ${({ theme }) => theme.shadows['2xl']};
    
    &::before {
      opacity: 1;
    }
  }
`;

const CategoryIcon = styled.div`
  width: 80px;
  height: 80px;
  margin: 0 auto ${({ theme }) => theme.spacing.lg};
  background: ${({ theme }) => theme.colors.accent.primary};
  border-radius: ${({ theme }) => theme.borderRadius.full};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: ${({ theme }) => theme.typography.sizes['3xl']};
  color: white;
`;

const CategoryName = styled.h3`
  font-size: ${({ theme }) => theme.typography.sizes.xl};
  font-weight: ${({ theme }) => theme.typography.weights.bold};
  margin-bottom: ${({ theme }) => theme.spacing.sm};
  color: ${({ theme }) => theme.colors.black};
`;

const CategoryDescription = styled.p`
  color: ${({ theme }) => theme.colors.grey[600]};
  font-size: ${({ theme }) => theme.typography.sizes.base};
`;

const BestSellersSection = styled(Section)`
  background: white;
`;

const CarouselContainer = styled.div`
  position: relative;
  overflow: hidden;
  padding: ${({ theme }) => theme.spacing.xl} 0;
`;

const CarouselTrack = styled(motion.div)`
  display: flex;
  gap: ${({ theme }) => theme.spacing.xl};
  padding: ${({ theme }) => theme.spacing.lg};
`;

const CarouselItem = styled(motion.div)`
  min-width: 300px;
  background: white;
  border-radius: ${({ theme }) => theme.borderRadius.xl};
  box-shadow: ${({ theme }) => theme.shadows.lg};
  overflow: hidden;
  transition: ${({ theme }) => theme.transitions.base};
  cursor: pointer;
  
  &:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: ${({ theme }) => theme.shadows['2xl']};
  }
`;

const CarouselImage = styled.div`
  width: 100%;
  height: 200px;
  background: ${({ image }) => `url(${image}) center/cover`};
  position: relative;
  overflow: hidden;
  
  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, transparent 30%, rgba(255, 255, 255, 0.1) 50%, transparent 70%);
    transform: translateX(-100%);
    transition: ${({ theme }) => theme.transitions.base};
  }
  
  ${CarouselItem}:hover &::after {
    transform: translateX(100%);
  }
`;

const QuickViewButton = styled.button`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) scale(0);
  background: ${({ theme }) => theme.colors.accent.primary};
  color: white;
  border: none;
  padding: ${({ theme }) => theme.spacing.sm} ${({ theme }) => theme.spacing.md};
  border-radius: ${({ theme }) => theme.borderRadius.full};
  font-weight: ${({ theme }) => theme.typography.weights.semibold};
  cursor: pointer;
  transition: ${({ theme }) => theme.transitions.base};
  z-index: 2;
  
  ${CarouselItem}:hover & {
    transform: translate(-50%, -50%) scale(1);
  }
  
  &:hover {
    background: ${({ theme }) => theme.colors.accent.dark};
    transform: translate(-50%, -50%) scale(1.1);
  }
`;

const CarouselContent = styled.div`
  padding: ${({ theme }) => theme.spacing.lg};
`;

const CarouselTitle = styled.h4`
  font-size: ${({ theme }) => theme.typography.sizes.lg};
  font-weight: ${({ theme }) => theme.typography.weights.bold};
  margin-bottom: ${({ theme }) => theme.spacing.sm};
  color: ${({ theme }) => theme.colors.black};
`;

const CarouselPrice = styled.p`
  font-size: ${({ theme }) => theme.typography.sizes.base};
  color: ${({ theme }) => theme.colors.accent.primary};
  font-weight: ${({ theme }) => theme.typography.weights.semibold};
`;

const InteractiveBanner = styled(Section)`
  background: linear-gradient(135deg, ${({ theme }) => theme.colors.grey[100]}, ${({ theme }) => theme.colors.grey[200]});
`;

const BannerContent = styled.div`
  text-align: center;
  max-width: 600px;
  margin: 0 auto;
`;

const BannerTitle = styled.h3`
  font-size: ${({ theme }) => theme.typography.sizes['4xl']};
  font-weight: ${({ theme }) => theme.typography.weights.bold};
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  color: ${({ theme }) => theme.colors.black};
`;

const BannerDescription = styled.p`
  font-size: ${({ theme }) => theme.typography.sizes.lg};
  color: ${({ theme }) => theme.colors.grey[600]};
  margin-bottom: ${({ theme }) => theme.spacing.xl};
`;

const ColorSwatches = styled.div`
  display: flex;
  gap: ${({ theme }) => theme.spacing.md};
  justify-content: center;
  margin-bottom: ${({ theme }) => theme.spacing.xl};
`;

const ColorSwatch = styled(motion.button)`
  width: 50px;
  height: 50px;
  border-radius: ${({ theme }) => theme.borderRadius.full};
  border: 3px solid white;
  cursor: pointer;
  box-shadow: ${({ theme }) => theme.shadows.md};
  transition: ${({ theme }) => theme.transitions.base};
  
  &:hover {
    transform: scale(1.2);
    box-shadow: ${({ theme }) => theme.shadows.lg};
  }
`;

const CustomShoe = styled(motion.div)`
  width: 200px;
  height: 200px;
  margin: 0 auto;
  background: ${({ color }) => color};
  border-radius: ${({ theme }) => theme.borderRadius.xl};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: ${({ theme }) => theme.typography.sizes['2xl']};
  color: white;
  font-weight: ${({ theme }) => theme.typography.weights.bold};
  box-shadow: ${({ theme }) => theme.shadows.xl};
`;

const Home = () => {
  const [selectedColor, setSelectedColor] = useState('#ff6b35');
  const { scrollY } = useScroll();
  const featuredProducts = getFeaturedProducts();

  const colors = [
    '#ff6b35', // Orange
    '#2196f3', // Blue
    '#4caf50', // Green
    '#9c27b0', // Purple
    '#f44336', // Red
  ];

  const categories = [
    { name: 'Men', icon: '👞', description: 'Classic and contemporary styles for men' },
    { name: 'Women', icon: '👠', description: 'Elegant and trendy designs for women' },
    { name: 'Sports', icon: '⚽', description: 'Performance footwear for athletes' },
    { name: 'Casual', icon: '👟', description: 'Comfortable everyday shoes' },
  ];

  const bestSellers = [
    { id: 1, name: 'Classic Sneaker', price: '$89.99', image: 'https://images.unsplash.com/photo-1549298916-b41d501d3772?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80' },
    { id: 2, name: 'Running Shoe', price: '$129.99', image: 'https://images.unsplash.com/photo-1595950653106-6c9ebd614d3a?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80' },
    { id: 3, name: 'Formal Oxford', price: '$159.99', image: 'https://images.unsplash.com/photo-1614252235316-8c857d38b5f4?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80' },
    { id: 4, name: 'Hiking Boot', price: '$199.99', image: 'https://images.unsplash.com/photo-1543508282-6319a3e2621f?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80' },
    { id: 5, name: 'Slip-on Loafers', price: '$79.99', image: 'https://images.unsplash.com/photo-1560769629-975ec94e6a86?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80' },
  ];

  const y1 = useTransform(scrollY, [0, 300], [0, -100]);
  const y2 = useTransform(scrollY, [0, 300], [0, 100]);

  return (
    <HomeContainer>
      <HeroSection>
        <FloatingShoe
          style={{ y: y1 }}
          animate={{ rotate: 360 }}
          transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
        />
        <FloatingShoe
          style={{ y: y2, right: '10%', top: '20%' }}
          animate={{ rotate: -360 }}
          transition={{ duration: 25, repeat: Infinity, ease: "linear" }}
        />
        
        <HeroContent>
          <HeroTitle
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Step Into Style
          </HeroTitle>
          <HeroSubtitle
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Discover the perfect blend of comfort and fashion with our premium collection
          </HeroSubtitle>
          <HeroButtons
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            <PrimaryButton to="/products">
              Shop Now
              <FiArrowRight />
            </PrimaryButton>
            <SecondaryButton to="/products">
              New Arrivals
              <FiArrowRight />
            </SecondaryButton>
          </HeroButtons>
        </HeroContent>
      </HeroSection>

      <Section>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Featured Products
          </SectionTitle>
          <FeaturedGrid
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            {featuredProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </FeaturedGrid>
        </Container>
      </Section>

      <CategorySection>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Shop by Category
          </SectionTitle>
          <CategoryGrid>
            {categories.map((category, index) => (
              <CategoryCard
                key={category.name}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ y: -8 }}
              >
                <CategoryIcon>{category.icon}</CategoryIcon>
                <CategoryName>{category.name}</CategoryName>
                <CategoryDescription>{category.description}</CategoryDescription>
              </CategoryCard>
            ))}
          </CategoryGrid>
        </Container>
      </CategorySection>

      <BestSellersSection>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Best Sellers
          </SectionTitle>
          <CarouselContainer>
            <CarouselTrack
              animate={{ x: [0, -1000, 0] }}
              transition={{ duration: 30, repeat: Infinity, ease: "linear" }}
            >
              {[...bestSellers, ...bestSellers].map((item, index) => (
                <CarouselItem key={`${item.id}-${index}`}>
                  <CarouselImage image={item.image}>
                    <QuickViewButton>
                      <FiEye /> Quick View
                    </QuickViewButton>
                  </CarouselImage>
                  <CarouselContent>
                    <CarouselTitle>{item.name}</CarouselTitle>
                    <CarouselPrice>{item.price}</CarouselPrice>
                  </CarouselContent>
                </CarouselItem>
              ))}
            </CarouselTrack>
          </CarouselContainer>
        </Container>
      </BestSellersSection>

      <InteractiveBanner>
        <Container>
          <BannerContent
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <BannerTitle>Customize Your Shoe</BannerTitle>
            <BannerDescription>
              Choose from our selection of premium colors and create your perfect pair
            </BannerDescription>
            
            <ColorSwatches>
              {colors.map((color) => (
                <ColorSwatch
                  key={color}
                  style={{ backgroundColor: color }}
                  onClick={() => setSelectedColor(color)}
                  whileHover={{ scale: 1.2 }}
                  whileTap={{ scale: 0.9 }}
                />
              ))}
            </ColorSwatches>
            
            <CustomShoe
              color={selectedColor}
              animate={{ rotate: [0, 5, -5, 0] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              👟
            </CustomShoe>
          </BannerContent>
        </Container>
      </InteractiveBanner>
    </HomeContainer>
  );
};

export default Home;
